mapper.select:
	select

 	from zhenghe_doctor as a



				(
					/*医生姓名*/
					a.doctor_name like concat('%',#{keyword},'%')
					/*医院名称*/
					or h.hospital_name like concat('%',#{keyword},'%')
					/*一级科室*/
					or (select dd.departments_name from zhenghe_departments as dd where id=dept.s_departments_id) like concat('%',#{keyword},'%')
					/*二级科室*/
					or a.departments_id in (select id from zhenghe_departments as dept where dept.departments_name like concat('%',#{keyword},'%'))
					/*医生职称*/
					or j.profession like concat('%',#{keyword},'%')
				)



				and a.departments_id=#{secondDepartmentsId}



				and a.departments_id in (select id from zhenghe_departments where s_departments_id=#{firstDepartmentsId})



				and h.city_id=#{cityId}



				and h.provincial_id=#{provincialId}

			and a.type = '1'

select

from zhenghe_doctor a

		type=#{type}

order by rank asc limit 3;

SELECT 

FROM zhenghe_doctor a

WHERE a.id = #{id}

SELECT
FROM zhenghe_doctor a
AND a.hospital_id LIKE '%'||#{hospitalId}||'%' '%'+#{hospitalId}+'%' concat('%',#{hospitalId},'%')
AND a.doctor_name LIKE '%'||#{doctorName}||'%' '%'+#{doctorName}+'%' concat('%',#{doctorName},'%')
AND a.gender = #{gender}
AND a.departments_id LIKE '%'||#{departmentsId}||'%' '%'+#{departmentsId}+'%' concat('%',#{departmentsId},'%')
AND a.professional_field LIKE '%'||#{professionalField}||'%' '%'+#{professionalField}+'%' concat('%',#{professionalField},'%')
AND a.job_title LIKE '%'||#{jobTitle}||'%' '%'+#{jobTitle}+'%' concat('%',#{jobTitle},'%')
AND a.status = #{status}
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.rank ASC
