mapper.select:
SELECT 

FROM zhenghe_life_log a

WHERE a.id = #{id}

SELECT 

FROM zhenghe_life_log a




		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.content = #{content}


		AND a.patient_id = #{patientId}


		AND a.create_date = #{createDate}


		AND a.standby1 = #{standby1}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
