mapper.select:
select 

from zhenghe_message a

	patient_id=#{patientId}
	doctor_id=#{doctorId}

SELECT 

FROM zhenghe_message a

WHERE a.id = #{id}

SELECT
FROM zhenghe_message a
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.content LIKE '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
AND a.status = #{status}
AND a.type = #{type}
AND a.differentiate_id = #{differentiateId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
