mapper.select:
SELECT 

FROM zhenghe_order a

WHERE a.id = #{id}

SELECT 

FROM zhenghe_order a



		and a.patient_id = #{patientId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone = #{phone}


		AND a.parent_order_no = #{parentOrderNo}


		AND a.status = #{status}


		AND a.express_no = #{expressNo}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
