mapper.select:
SELECT 

FROM zhenghe_order_item a

WHERE a.id = #{id}

SELECT 

FROM zhenghe_order_item a




		AND a.product_id = #{productId}


		AND a.order_id = #{orderId}


		AND a.order_item_no LIKE 
			'%'||#{orderItemNo}||'%'
			'%'+#{orderItemNo}+'%'
			concat('%',#{orderItemNo},'%')


		AND a.product_name LIKE 
			'%'||#{productName}||'%'
			'%'+#{productName}+'%'
			concat('%',#{productName},'%')


		AND a.original_price = #{originalPrice}


		AND a.reality_price = #{realityPrice}


		AND a.sum_price = #{sumPrice}


		AND a.count = #{count}


		AND a.doctor_id = #{doctorId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
