mapper.select:
SELECT 

FROM zhenghe_product a left join zhenghe_product_classify b on a.pclassify_id = b.id

		a.pclassify_id = #{classifyId} and a.status=1

			and a.type = #{type}


			and a.price ${priceBetween}


ORDER BY ${orderBy} limit #{pageNo},#{pageSize}

SELECT 

FROM zhenghe_product a left join zhenghe_product_classify b on a.pclassify_id = b.id

		a.product_name like concat('%',#{keys},'%') and a.status=1

			and a.pclassify_id = #{classifyId}


			and a.type = #{type}


			and a.price ${priceBetween}


ORDER BY ${orderBy} limit #{pageNo},#{pageSize}

SELECT 

FROM zhenghe_product a left join zhenghe_product_classify b on a.pclassify_id = b.id

WHERE a.id = #{id}

SELECT 

FROM zhenghe_product a left join zhenghe_product_classify b on a.pclassify_id = b.id



		AND a.pclassify_id = #{pclassifyId}


		AND a.product_name LIKE 
			'%'||#{productName}||'%'
			'%'+#{productName}+'%'
			concat('%',#{productName},'%')


		AND a.type = #{type}


		AND a.pfunction LIKE 
			'%'||#{pfunction}||'%'
			'%'+#{pfunction}+'%'
			concat('%',#{pfunction},'%')


		AND a.repertory_num < #{repertoryNum}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
